<style>
  ul {
    line-height: 0;
    min-height: calc((var(--vh, 1vh) * 100) - 11rem);
    display: flex;
    flex-wrap: wrap;
  }

  li {
    position: relative;
    width: 50%;
    min-height: 200px;
  }

  @media (--max-content-width) {
    ul {
      flex-wrap: nowrap;
      min-height: calc((var(--vh, 1vh) * 100) - 12.5rem);
    }
  }

  a {
    cursor: pointer;
    display: inline-block;
    height: 100%;
    width: 100%;
    background-color: var(--main-color);
    transition: background-color 300ms ease-in-out, color 300ms ease-in-out;
  }

  :global(.user-is-tabbing) a:focus .text-svg {
    fill: var(--light);
  }

  :global(.user-is-tabbing) a:focus .icon-svg {
    fill: var(--secondary-color);
  }

  @media (--not-touchscreen) {
    a:hover .text-svg {
      fill: var(--light);
    }

    a:hover .icon-svg {
      fill: var(--secondary-color);
    }
  }

  svg {
    position: absolute;
    width: calc(100% - 3rem);
    height: auto;
    max-height: 80%;
    top: 50%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%);
  }

  .text-svg {
    fill: var(--dark);
    max-width: 18rem;
    transition: fill 300ms ease-in-out;
  }

  .icon-svg {
    fill: var(--light);
    width: calc(100% - 2rem);
    height: 80%;
    transition: fill 300ms ease-in-out;
  }
</style>
